precision highp float;
varying vec2 v_uv;
     uniform mediump vec4 texSize;
uniform sampler2D bloomTexture;
vec3 downsample4taps(vec2 uv, vec2 halfpixel) {
  vec3 sum = texture2D(bloomTexture, uv + vec2(-halfpixel.x, halfpixel.y)).xyz;
      sum += texture2D(bloomTexture, uv + vec2(halfpixel.x, halfpixel.y)).xyz;
      sum += texture2D(bloomTexture, uv + vec2(halfpixel.x, -halfpixel.y)).xyz;
      sum += texture2D(bloomTexture, uv + vec2(-halfpixel.x, -halfpixel.y)).xyz;
  return sum / 4.0;
}
void main()
{
  vec3 result = downsample4taps(v_uv, 1.0 / texSize.xy).rgb;
  gl_FragColor = vec4(result, 1.0);
}

