        if (cameraPos.y > fogTop) {
            if (wPos.y < fogTop) {
                fDeltaY = (fogTop - wPos.y) / fogRange * 2.0;
                fDensityIntegral = fDeltaY * fDeltaY * 0.5;
            }
            else {
                fDeltaY = 0.;
                fDensityIntegral = 0.;
            }
