        float f = exp(-cam_dis * cam_dis * fogDensity * fogDensity);
        return f;
    }
    float LayeredFog(vec4 pos, vec3 cameraPos, float fogTop, float fogRange, float fogAtten) {
        vec4 wPos = pos;
