  #endif
  #if FIX_ANISOTROPIC_ROTATION_MAP
    #if IS_ANISOTROPY && USE_ANISOTROPY_MAP
      vec4 reference = texture2D(anisotropyMapNearestFilter, DEFAULT_UV);
      vec2 oneTap = vec2(0.0, 1.0 / anisotropyParam.w);
      float threshold = 0.2;
      vec4 sample1 = texture2D(anisotropyMapNearestFilter, DEFAULT_UV + oneTap);
      vec4 sample2 = texture2D(anisotropyMapNearestFilter, DEFAULT_UV - oneTap);
      if (abs(sample1.y - reference.y) > threshold || abs(sample2.y - reference.y) > threshold) {
          tex.y = reference.y;
